package soggySamosaAkaBlanket;

public class DayFour {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String str = "Hi Guys";
		System.out.println(str.charAt(4));
		System.out.println(str.contains("i"));
		System.out.println(str.equals("Hi Guys"));
		System.out.println(str.endsWith("ys"));
		System.out.println(str.indexOf("G"));
		System.out.println();
		System.out.println(str.equalsIgnoreCase("HI gUyS"));
		System.out.println(str.length());
		System.out.println(str.isEmpty());
		System.out.println(str.replace("Hi", "Bye"));
		System.out.println(str.substring(0,5));
		System.out.println(str.substring(4));
		System.out.println(str.toUpperCase());
		System.out.println(str.toLowerCase());
		
		
	}

}
